mapper.select:
select * from (select rownum as rn, a.* from(select * from MEMBER where
id like '%'||#{searchName}||'%' name like '%'||#{searchName}||'%' phone like '%'||#{searchName}||'%' 1=1
order by id desc)a )where rn >(#{pageNum}-1)*#{amount} and rn <=#{pageNum}*#{amount}

select * from (select rownum as rn, a.* from(select * from PRODUCT where
pid like '%'||#{searchName}||'%' pname like '%'||#{searchName}||'%' 1=1
order by pid desc)a )where rn >(#{pageNum}-1)*#{amount} and rn <=#{pageNum}*#{amount}

select * from (select rownum as rn, a.* from(select * from SALES where
salespid like '%'||#{searchName}||'%' salespname like '%'||#{searchName}||'%' 1=1
order by salesdate desc)a )where rn >(#{pageNum}-1)*#{amount} and rn <=#{pageNum}*#{amount}
